#include &lt;vector&gt;#include &lt;iostream&gt;using namespace std;vector&lt;int&gt; solution(vector&lt;int&gt; arr) {vector&lt;int&gt; answer;for (int i : arr) {if (answer.size() &gt; 0) {if (answer.back() != i) {answer.push_back(i);}}else {answer.push_back(i);}}return answer;}

