#include &lt;iostream&gt;#include &lt;algorithm&gt;#include &lt;vector&gt;using namespace std;vector&lt;int&gt;d;int main(void) {int n, s, l;cin &gt;&gt; n;for (int i = 0; i &lt; n; i++) {cin &gt;&gt; s;l = lower_bound(d.begin(), d.end(), s) - d.begin();if (l == d.size())d.push_back(s);elsed[l] = s;}cout &lt;&lt; n - d.size() &lt;&lt; endl;return 0;}

