풀이:  https://jyukki97.github.io/2098/ 과 같으므로 참고  코드: 사용언어 : c++
#include &lt;iostream&gt;#include &lt;algorithm&gt;using namespace std;int w[17][17],d[17][100000], n;int T(int a, int b) {if ((1 &lt;&lt; n) - 1 == b &amp;&amp; w[a][0] != 0)return w[a][0];int &amp;r = d[a][b];if (r &gt; 0)return r;r = 100000000;for (int i = 0; i &lt; n; i++)if (w[a][i] != 0 &amp;&amp; (b &amp; (1 &lt;&lt; i)) == 0)r = min(r, w[a][i] + T(i, b | (1 &lt;&lt; i)));return r;}int main(void) {cin &gt;&gt; n;for (int i = 0; i &lt; n; i++)for (int t = 0; t &lt; n; t++)cin &gt;&gt; w[i][t];cout &lt;&lt; T(0, 1) &lt;&lt; endl;return 0;}
